import React, { useEffect, useState } from "react";
import { POSTS, USERS } from "./type";
import dayjs from "dayjs";
import Swal from "sweetalert2";
import axios, { AxiosError } from "axios";

let buddhistEra = require("dayjs/plugin/buddhistEra");
dayjs.extend(buddhistEra);

function App() {
  //---------------
  // STATE
  //---------------
  const [users, setUsers] = useState<Array<USERS>>();
  const [posts, setPosts] = useState<Array<POSTS>>();

  //---------------
  // EFFECT
  //---------------
  useEffect(() => {
    getUsers();
    getPosts();
  }, []);

  //---------------
  // HANDLE
  //---------------
  const getUsers = async () => {
    try {
      const resp = await axios("https://maqe.github.io/json/authors.json");
      if (resp.status === 200) {
        setUsers(resp.data);
      }
    } catch (e) {
      let err = e as AxiosError;
      Swal.fire({
        title: "Error Fetching Data",
        text: err.message,
      });
    }
  };

  const getPosts = async () => {
    try {
      const resp = await axios("https://maqe.github.io/json/posts.json");
      if (resp.status === 200) {
        setPosts(resp.data);
      }
    } catch (e) {
      let err = e as AxiosError;
      Swal.fire({
        title: "Error Fetching Data",
        text: err.message,
      });
    }
  };
  //---------------
  // RENDER
  //---------------
  return (
    <div className="bg-gray-300 w-full min-h-screen">
      <h1 className="">MAQE Forum</h1>
    </div>
  );
}

export default App;
