import React, { useEffect, useState } from "react";
import dayjs from "dayjs";
import Swal from "sweetalert2";
import axios, { AxiosError } from "axios";
import { POSTS, USERS } from "./type";

let buddhistEra = require("dayjs/plugin/buddhistEra");
dayjs.extend(buddhistEra);

function App() {
  //---------------
  // STATE
  //---------------
  const [users, setUsers] = useState<Array<USERS>>([]);
  const [posts, setPosts] = useState<Array<POSTS>>([]);

  //---------------
  // EFFECT
  //---------------
  useEffect(() => {
    getUsers();
    getPosts();
  }, []);

  //---------------
  // HANDLE
  //---------------
  const getUsers = async () => {
    try {
      const resp = await axios("https://maqe.github.io/json/authors.json");
      if (resp.status === 200) {
        setUsers(resp.data);
      }
    } catch (e) {
      let err = e as AxiosError;
      Swal.fire({
        title: "Error Fetching Data",
        text: err.message,
      });
    }
  };

  const getPosts = async () => {
    try {
      const resp = await axios("https://maqe.github.io/json/posts.json");
      if (resp.status === 200) {
        setPosts(resp.data);
      }
    } catch (e) {
      let err = e as AxiosError;
      Swal.fire({
        title: "Error Fetching Data",
        text: err.message,
      });
    }
  };
  //---------------
  // RENDER
  //---------------
  return (
    <div className="bg-gray-200">
      <div className="min-h-screen mx-auto p-6 desktop:pt-6 desktop:w-[1440px]">
        <div className=" w-full h-full">
          <p className="text-2xl font-bold">MAQE Forum</p>
          <p className="text-2xl mt-6">
            Your current timezone is: Asia/Bangkok
          </p>
          <div className="mt-4 max-h-[38rem] desktop:h-full overflow-y-scroll">
            {posts.length > 0 &&
              posts.map((item) => (
                <div className="bg-white shadow-md w-full min-h-[260px] mb-4">
                  <div className="border-b px-4 py-3 flex items-center space-x-2">
                    <img
                      src={users[0]?.avatar_url}
                      className="w-6 rounded-full"
                    />
                    <p className="text-orange-600 font-medium">
                      {users[0]?.name}
                    </p>
                    <p className="text-gray-300 font-medium">{`posted on ${dayjs().format(
                      "dddd, MMMM D, YYYY, HH:mm"
                    )}`}</p>
                  </div>
                  <div className="p-4 flex">
                    <img
                      src={item.image_url || ""}
                      className="h-full w-[280px] flex-shrink-0"
                    />
                    <div className="ml-4">
                      <p className="font-semibold text-[20px]">
                        {item.title || "-"}
                      </p>
                      <p className="font-light">{item.body || "-"}</p>
                    </div>
                  </div>
                </div>
              ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
